body.script:
        $(function () {			
            $('#scrollToBottom').bind("click", function () {
                $('html, body').animate({ scrollTop: $(document).height() }, 1200);
                return false;
            });
			$(window).scroll(function () {
        if ($(this).scrollTop() > 100) {
            $('#scrollToTop').fadeIn();
        } else {
            $('#scrollToTop').fadeOut();
        }
    });
   $('#scrollToTop').bind("click", function () {
                $('html, body').animate({ scrollTop: 0 }, 1200);
                return false;
            });
        });

$("#showmenu").click(function(e){

			e.preventDefault();
			var $div = $('#mobilemenu').attr('class');
			if($div=='mobilenavright'){
				$("#change_icon").removeClass("fa fa-align-justify");
                 $("#change_icon").addClass("fa fa-close");

			}else{
                 $("#change_icon").removeClass("fa fa-close");
                $("#change_icon").addClass("fa fa-align-justify");

            }


			$("#mobilemenu").toggleClass("show");
			$("#showmenu").toggleClass("menuclosebtn");
		});
		$(".slide08").click(function(event){

			event.preventDefault();
			if($(this).next('ul').length){


				$(this).next().toggle('fast');
				$(this).children('i:last-child').toggleClass('fa-caret-down fa-caret-left');
			}
		});

$("#esubmit").click(function(e) {

	var ename = $("#ename").val();
	var eorganization = $("#eorganization").val();
	var ephone = $("#ephone").val();
	var eemail = $("#eemail").val();
	var message = $("#message").val();
	var message = $("#message").val();
	var ebudget = $("#ebudget").val();

    var serviceslist=[];

	   $('input[name=option1]:checked').each(function(){	
			  var options_all = $(this).val(); 
			serviceslist.push(options_all);		 
	 });

	var em = /^[_a-zA-Z0-9-]+(\.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(\.[a-zA-Z0-9-]+)*(\.[a-zA-Z]{2,4})$/;

	var na = /^[a-zA-Z ]*$/;

	var mo = /^([0-9\(\)\/\+ \-]*)$/;

// 	var mo = /^((\+\d{1,3}(-| )?\(?\d\)?(-| )?\d{1,3})|(\(?\d{2,3}\)?))(-| )?(\d{3,4})(-| )?(\d{4})(( x| ext)\d{1,5}){0,1}$/;

	var or = /^[a-zA-Z 0-9]*$/;	

	var regadd =/^[A-Za-z0-9\.\-\#\&\,\:\/ ]*$/;

	 if($("input[name='option1']").is(":checked") == false ){

	    $("#option1_lbl").html("This field is required.").fadeIn().fadeOut(4000);
		$("#option1").focus();
		return false;	 

	 } else if(ename.length == '') {		

		$("#ename_lbl").html("This field is required.").fadeIn().fadeOut(4000);
		$("#ename").focus();
		return false;

	} else if(!na.test(ename)) {

		$("#ename_lbl").html("Special characters and digits not allowed.").fadeIn().fadeOut(4000);
		$("#ename").focus();
		return false;

	} 

	if(eorganization != ""){

		if(!or.test(eorganization)) {

			$("#ename_lbl").html(""); 
			$("#eorganization_lbl").html("Special characters and digits not allowed.").fadeIn().fadeOut(4000);
			$("#eorganization").focus();
			return false;

		} 

	}


	if(ephone.length == '') {		

		$("#ename_lbl").html(""); 
		$("#eorganization_lbl").html(""); 
		$("#ephone_lbl").html("This field is required.").fadeIn().fadeOut(4000);
		$("#ephone").focus();
		return false;

	} else if(!mo.test(ephone)) {

		$("#eorganization_lbl").html(""); 
		$("#ephone_lbl").html("Please enter valid mobile number.").fadeIn().fadeOut(4000);
		$("#ephone").focus();
		return false;

	} else if(eemail.length == '') {

		$("#ephone_lbl").html("");  
		$("#eemail_lbl").html("This field is required.").fadeIn().fadeOut(4000);
		$("#eemail").focus();
		return false;

	} else if(!em.test(eemail)) {

		$("#phone_lbl").html(""); 
		$("#eemail_lbl").html("Please enter valid email.").fadeIn().fadeOut(4000);
		$("#eemail").focus();
		return false;

	} else if(ebudget.length == '') {

		$("#eemail_lbl").html("");  
		$("#ebudget_lbl").html("This field is required.").fadeIn().fadeOut(4000);
		$("#ebudget").focus();
		return false;

	} else if(message.length == '') {

		$("#phone_lbl").html("");  
		$("#message_lbl").html("This field is required.").fadeIn().fadeOut(4000);
		$("#message").focus();
		return false;

	} else if(!regadd.test(message)) {

		$("#phone_lbl").html(""); 
		$("#message_lbl").html("Some Special characters not allowed.").fadeIn().fadeOut(4000);
		$("#message").focus();
		return false;

	} else {

		           var dataString = "serviceslist="+serviceslist+"&ename="+ename+"&eorganization="+eorganization+"&ephone="+ephone+"&eemail="+eemail+"&message="+message+"&ebudget="+ebudget;

				  // alert(dataString);				   				   				     
			// $("#eflash").show();
					 //$("#eflash").fadeIn(100).html('');
					  $.ajax({
					  type: "POST",
					  url: "https://www.inovies.com/enquiryprocess",
					  data: dataString,					
					  cache: false,
					  success:function(data){
						  //  alert(data);					  
					  //$("#eflash").hide();
					  var tim = data.split('@');
					  if(tim[0] == 1) {						  

						 $("#msg").fadeOut(5000).html(tim[1]);
						 setTimeout(function(){  window.location.href="https://www.inovies.com/thank-you"; }, 3000);

					  } else if(tim[0] == 0){					  

						   $("#msg").fadeOut(5000).html(tim[1]);					

					  }

					  $('#enquiry_from')[0].reset();

				   }

                 });

		  return false; 			 

	  }   	  
});

